import Router from 'express';
import multiparty from 'connect-multiparty';
import paginateMiddleware from '../middlewares/paginateMiddleware';
import employeeController from '../controllers/employeeController';
import awsUploadFileService from '../services/awsUploadFileService';
import { validateUpdateEmployee } from '../middlewares/schemaMiddleware';

const multipart = multiparty();
const employeesRouter = Router();

employeesRouter
  .get('/select-employee/:employeeId', employeeController.selectEmployee)
  .get('/select-employees', employeeController.selectEmployees, paginateMiddleware.paginateData)

  .delete('/delete-employee/:employeeId', employeeController.deleteEmployee)
  .patch('/update-employee/:employeeId', multipart, awsUploadFileService, validateUpdateEmployee, employeeController.updateEmployee);

export default employeesRouter;

import { INTERNAL_SERVER_ERROR, OK } from 'http-status';
import responseHelper from '../helpers/responseHelper';
/**
* This class contains method (functions) required to handle
* paginateData function.
*/
class PaginateData {
  /**
     * Handle paginateData.
     * @param {object} req user request.
     * @param {object} res data response.
     * @returns {object} returned object of data.
     */
  static paginateData(req, res) {
    try {
      req.data.paginate.paginate = req.data.allDatata;
      if (req.data.start > 0) {
        req.data.paginate.Previous = {
          page: req.data.pages - 1,
          limit: req.data.skip,
        };
      } if (req.data.end < req.data.countAllData) {
        req.data.paginate.Next = {
          page: req.data.pages + 1,
          limit: req.data.skip,
        };
      }

      const data = req.data.paginate;
      responseHelper.handleSuccess(OK, `Page ${req.data.pages || null} Selected successfully`, data);
      return responseHelper.response(res);
    } catch (error) {
      responseHelper.handleError(INTERNAL_SERVER_ERROR, error.toString());
      return responseHelper.response(res);
    }
  }
}
export default PaginateData;

/**
* This class contains all methods (functions) required to handle
* handleSuccess function.
* handleError function.
* response function.
*/
class ResponseHelpers {
  /**
   * Handle Success Response.
   * @param {integer} statusCode status code.
   * @param {integer} message message.
   * @param {object} data data.
   * @returns {null} .
   */
  static handleSuccess(statusCode, message, data) {
    this.statusCode = statusCode;
    this.message = message;
    this.data = data;
    this.type = 'success';
  }

  /**
   * Handle Error Response.
   * @param {integer} statusCode status code.
   * @param {integer} message message.
   * @returns {null} .
   */
  static handleError(statusCode, message) {
    this.statusCode = statusCode;
    this.message = message;
    this.type = 'error';
  }

  /**
   * Handle response output.
   * @param {object} res response.
   * @returns {object} response.
   */
  static response(res) {
    if (this.type === 'success') {
      return res.status(this.statusCode).json({
        status: this.statusCode,
        message: this.message,
        data: this.data,
      });
    }
    return res.status(this.statusCode).json({
      status: this.statusCode,
      message: this.message,
    });
  }
}

export default ResponseHelpers;

import { INTERNAL_SERVER_ERROR, NOT_FOUND, BAD_REQUEST, OK } from 'http-status';
import employeeHelper from '../helpers/employeeHelper';
import responseHelper from '../helpers/responseHelper';
import pagination from '../helpers/paginateHelper';

/**
* This class contains all methods (functions) required to handle
* Select all employees function.
* Select a particular employee function.
* Update a particular employee function.
* Delete a particular employee function.
*/
class EmployeeController {
  /**
     * Handle selectEmployee.
     * @param {object} req user request.
     * @param {object} res data response.
     * @returns {object} returned employee details object.
     */
  static async selectEmployee(req, res) {
    try {
      const data = await employeeHelper.findOneEmployees('_id', req.params.employeeId);
      if (!data) {
        responseHelper.handleError(NOT_FOUND, `Employee with id ${req.params.employeeId} not found.`);
        return responseHelper.response(res);
      }

      responseHelper.handleSuccess(OK, 'Employee details selected successfully.', data);
      return responseHelper.response(res);
    } catch (error) {
      responseHelper.handleError(INTERNAL_SERVER_ERROR, error.toString());
      return responseHelper.response(res);
    }
  }

  /**
     * Handle selectEmployees.
     * @param {object} req user request.
     * @param {object} res data response.
     * @param {object} next move data response.
     * @returns {object} returned object of data.
     */
  static async selectEmployees(req, res, next) {
    try {
      const { start, end, pages, skip, paginate } = await pagination.paginateData(req.query);
      const fetchedData = await employeeHelper.findEmployees(skip, start);
      const countedData = await employeeHelper.countEmployees();

      const allDatata = fetchedData;
      const countAllData = countedData;

      if (countAllData < 1) {
        responseHelper.handleError(NOT_FOUND, 'Employees not found at the moment');
        return responseHelper.response(res);
      }

      req.data = { allDatata, countAllData, start, end, pages, skip, paginate };
      next();
    } catch (error) {
      responseHelper.handleError(INTERNAL_SERVER_ERROR, error.toString());
      return responseHelper.response(res);
    }
  }

  /**
     * Handle deleteEmployee.
     * @param {object} req user request.
     * @param {object} res data response.
     * @returns {string} returned message.
     */
  static async deleteEmployee(req, res) {
    try {
      const data = await employeeHelper.findOneEmployees('_id', req.params.employeeId);
      if (!data) {
        responseHelper.handleError(NOT_FOUND, `Employee with id ${req.params.employeeId} not found.`);
        return responseHelper.response(res);
      }

      await employeeHelper.deleteOneEmployee('_id', req.params.employeeId);
      responseHelper.handleSuccess(OK, 'Employee deleted successfully.');
      return responseHelper.response(res);
    } catch (error) {
      responseHelper.handleError(INTERNAL_SERVER_ERROR, error.toString());
      return responseHelper.response(res);
    }
  }

  /**
     * Handle updateEmployee.
     * @param {object} req user request.
     * @param {object} res data response.
     * @returns {object} returned object of updated data.
     */
  static async updateEmployee(req, res) {
    try {
      if (req.body && Object.keys(req.body).length === 0 && Object.getPrototypeOf(req.body) === Object.prototype) {
        responseHelper.handleError(BAD_REQUEST, `Changes to update not found.`);
        return responseHelper.response(res);
      }

      let data = await employeeHelper.findOneEmployees('_id', req.params.employeeId);
      if (!data || data.isFired === true) {
        responseHelper.handleError(NOT_FOUND, `Employee with id ${req.params.employeeId} not found.`);
        return responseHelper.response(res);
      }

      const photo = req.photo !== 'Error' && req.photo !== 'TypeError' && req.photo !== undefined && req.photo !== null ? req.photo.Location : data.photo;
      data = await employeeHelper.updateOneEmployee(req.body, data, photo);
      responseHelper.handleSuccess(OK, 'Employee details updated successfully.', data);
      return responseHelper.response(res);
    } catch (error) {
      responseHelper.handleError(INTERNAL_SERVER_ERROR, error.toString());
      return responseHelper.response(res);
    }
  }
}

export default EmployeeController;

import EmployeeSchema from '../models/employeeSchema';
import DepertmentSchema from '../models/depertmentSchema';

/**
* This class contains all methods (functions) required to handle
* Select all employees function.
* Select a particular employee function.
* Update a particular employee function.
* Delete a particular employee function.
*/
class EmployeeHelper {
  /**
   * findOneEmployees from Employees table.
   * @param {any} attribute table column field.
   * @param {any} value table column value.
   * @returns {object} of employee detatils.
   */
  static async findOneEmployees(attribute, value) {
    const data = await EmployeeSchema.findOne({ [attribute]: value });
    return data;
  }

  /**
   * findOneDepertment from depertment table.
   * @param {any} attribute table column field.
   * @param {any} value table column value.
   * @returns {object} of depertment detatils.
   */
  static async findOneDepertment(attribute, value) {
    const data = await DepertmentSchema.findOne({ [attribute]: value });
    return data;
  }

  /**
   * findEmployees from Employees table.
   * @param {integer} skip limitation point.
   * @param {integer} start start point.
   * @returns {object} of all employees.
   */
  static async findEmployees(skip, start) {
    const data = await EmployeeSchema.find().skip(start).limit(skip);
    return data;
  }

  /**
   * countData from Employees table.
   * @returns {number} of all employees counted.
   */
  static async countEmployees() {
    const data = await EmployeeSchema.countDocuments();
    return data;
  }

  /**
   * deleteOneEmployee from Employees table.
   * @param {any} attribute table column field.
   * @param {any} value table column value.
   * @returns {null}.
   */
  static async deleteOneEmployee(attribute, value) {
    await EmployeeSchema.deleteOne({ [attribute]: value });
  }

  /**
   * updateOneEmployee from Employees table.
   * @param {object} newDetails new details of employee.
   * @param {object} oldDetails exist employee details.
   * @param {string} photo photo of employee.
   * @param {integer} depertmentId id of depertment.
   * @returns {object} updated employee details.
   */
  static async updateOneEmployee(newDetails, oldDetails, photo) {
    let data;
    if (newDetails.depertmentPosition !== undefined) {
      data = await this.findOneDepertment('depertmentPositions', newDetails.depertmentPosition);
    }

    data = await EmployeeSchema.updateOne({ _id: oldDetails.id },
      {
        $set: {
          name: newDetails.name || oldDetails.name,
          photo: photo || oldDetails.photo,
          email: newDetails.email || oldDetails.email,
          phone: newDetails.phone || oldDetails.phone,
          dob: newDetails.dob || oldDetails.dob,
          address: newDetails.address || oldDetails.address,
          salary: newDetails.salary || oldDetails.salary,
          employmentDate: newDetails.employmentDate || oldDetails.employmentDate,
          depertmentId: data === undefined ? oldDetails.depertmentId : data.id,
        }
      }
    );

    if (data.matchedCount === 1) return this.findOneEmployees('_id', oldDetails.id);
    return undefined;
  }

  /**
   * createEmployee from Employees table.
   * @param {object} body new details of employee.
   * @returns {object} updated employee details.
   */
  static async createEmployee(body) {
    let data;
    if (body.depertmentPosition !== undefined || body.depertmentPosition !== null) {
      data = await this.findOneDepertment('depertmentPositions', body.depertmentPosition);
    }
    data = await EmployeeSchema.create({
      isFired: false,
      name: body.name,
      photo: body.photo,
      email: body.email,
      phone: body.phone,
      dob: body.dob,
      address: body.address,
      salary: body.salary,
      employmentDate: body.employmentDate,
      depertmentId: data.id
    });

    return data;
  }
}

export default EmployeeHelper;

import mongoose from 'mongoose';

const Schema = mongoose.Schema;
const EmployeeSchema = new Schema({
  isFired: { type: Boolean, default: false },
  name: { type: String, required: [true, 'name is required'] },
  photo: { type: String, required: [true, 'photo is required'] },
  email: { type: String, required: [true, 'email is required'] },
  phone: { type: String, required: [true, 'phone is required'] },
  dob: { type: String, required: [true, 'birthDate is required'] },
  address: { type: String, required: [true, 'address is required'] },
  salary: { type: String, required: [true, 'salary is required'] },
  employmentDate: { type: String, required: [true, 'employmentDate is required'] },
  depertmentId: [{ type: Schema.Types.ObjectId, ref: 'Depertments' }],
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
});

export default mongoose.model('Employees', EmployeeSchema);

